pragma solidity >=0.7.0 <0.9.0;

import "../common/Enum.sol";
import "../common/SelfAuthorized.sol";

interface Guard {
    function checkTransaction(
        address to,
        uint256 value,
        bytes memory data,
        Enum.Operation operation,
        uint256 safeTxGas,
        uint256 baseGas,
        uint256 gasPrice,
        address gasToken,
        address payable refundReceiver,
        bytes memory signatures,
        address msgSender
    ) external;

    function checkAfterExecution(bytes32 txHash, bool success) external;
}

/// @title Fallback Manager - A contract that manages fallback calls made to this contract
/// @author Richard Meissner - <richard@gnosis.pm>
contract GuardManager is SelfAuthorized {
    event ChangedGuard(address guard);
    // keccak256("guard_manager.guard.address")
    bytes32 internal constant GUARD_STORAGE_SLOT = 0x4a204f620c8c5ccdca3fd54d003badd85ba500436a431f0cbda4f558c93c34c8;

    /// @dev Set a guard that checks transactions before execution
    /// @param guard The address of the guard to be used or the 0 address to disable the guard
    function setGuard(address guard) external authorized {
        bytes32 slot = GUARD_STORAGE_SLOT;
        // solhint-disable-next-line no-inline-assembly
        assembly {
            sstore(slot, guard)
        }
        emit ChangedGuard(guard);
    }

    function getGuard() internal view returns (address guard) {
        bytes32 slot = GUARD_STORAGE_SLOT;
        // solhint-disable-next-line no-inline-assembly
        assembly {
            guard := sload(slot)
        }
    }
}
","keccak256":"0x53a532a31f9632d5a73ad0df56f05bd2b66a2f781f571eb48d00367d370707f9","license":"LGPL-3.0-only"},"contracts/base/ModuleManager.sol":{"content":"// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.7.0 <0.9.0;
import "../common/Enum.sol";
import "../common/SelfAuthorized.sol";
import "./Executor.sol";

contract ModuleManager is SelfAuthorized, Executor {
    event EnabledModule(address module);
    event DisabledModule(address module);
    event ExecutionFromModuleSuccess(address indexed module);
    event ExecutionFromModuleFailure(address indexed module);

    mapping(address => bool) internal modules;

    /// @dev Allows to add a module to the whitelist.
    ///      This can only be done via a Safe transaction.
    /// @notice Enables the module `module` for the Safe.
    /// @param module Module to be whitelisted.
    function enableModule(address module) public authorized {
        // Module address cannot be null or sentinel.
        require(!modules[module], "SDMM101");
        modules[module] = true;
        emit EnabledModule(module);
    }

    /// @dev Allows to remove a module from the whitelist.
    ///      This can only be done via a Safe transaction.
    /// @notice Disables the module `module` for the Safe.
    /// @param prevModule Module that pointed to the module to be removed in the linked list
    /// @param module Module to be removed.
    function disableModule(
        address prevModule,
        address module
    ) public authorized {
        // Validate module address and check that it corresponds to module index.
        require(modules[module], "SDMM101");
        modules[module] = false;
        emit DisabledModule(module);
    }

    /// @dev Allows a Module to execute a Safe transaction without any further confirmations.
    /// @param to Destination address of module transaction.
    /// @param value Ether value of module transaction.
    /// @param data Data payload of module transaction.
    /// @param operation Operation type of module transaction.
    function execTransactionFromModule(
        address to,
        uint256 value,
        bytes memory data,
        Enum.Operation operation
    ) public virtual returns (bool success) {
        // Only whitelisted modules are allowed.
        require(
            modules[msg.sender],
            "SDMM104"
        );
        // Execute transaction without further confirmations.
        success = execute(to, value, data, operation, gasleft());
        if (success) emit ExecutionFromModuleSuccess(msg.sender);
        else emit ExecutionFromModuleFailure(msg.sender);
    }

    /// @dev Allows a Module to execute a Safe transaction without any further confirmations and return data
    /// @param to Destination address of module transaction.
    /// @param value Ether value of module transaction.
    /// @param data Data payload of module transaction.
    /// @param operation Operation type of module transaction.
    function execTransactionFromModuleReturnData(
        address to,
        uint256 value,
        bytes memory data,
        Enum.Operation operation
    ) public returns (bool success, bytes memory returnData) {
        success = execTransactionFromModule(to, value, data, operation);
        // solhint-disable-next-line no-inline-assembly
        assembly {
            // Load free memory location
            let ptr := mload(0x40)
            // We allocate memory for the return data by setting the free memory location to
            // current free memory location + data size + 32 bytes for data size value
            mstore(0x40, add(ptr, add(returndatasize(), 0x20)))
            // Store the size
            mstore(ptr, returndatasize())
            // Store the data
            returndatacopy(add(ptr, 0x20), 0, returndatasize())
            // Point the return data to the correct memory location
            returnData := ptr
        }
    }

    /// @dev Returns if an module is enabled
    /// @return True if the module is enabled
    function isModuleEnabled(address module) public view returns (bool) {
        return modules[module];
    }
}
","keccak256":"0x2165410be0398900eb844e30693f6ee1268d95364effce1de43e48205d5c8ce2","license":"LGPL-3.0-only"},"contracts/base/NonceManager.sol":{"content":"// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.7.0 <0.9.0;
import "../common/SelfAuthorized.sol";
import "../common/StorageHelper.sol";
import "../interfaces/INonceManager.sol";

contract NonceManager is SelfAuthorized, INonceManager {
    // NONCE_KEY = keccak256("org.sodium.base.nonce");
    bytes32 private constant NONCE_KEY =
        bytes32(
            0x1ec7ef52f8bbd89fae80c55c6884305d3fb5ef41070638221dac337a4377b7df
        );

    uint256 private constant NONCE_BITS = 96;
    bytes32 private constant NONCE_MASK = bytes32((1 << NONCE_BITS) - 1);

    /**
     * @notice Decodes a raw nonce
     * @dev A raw nonce is encoded using the first 160 bits for the space
     *  and the last 96 bits for the nonce
     * @param _rawNonce Nonce to be decoded
     * @return _space The nonce space of the raw nonce
     * @return _nonce The nonce of the raw nonce
     */
    function _decodeNonce(
        uint256 _rawNonce
    ) private pure returns (uint256 _space, uint256 _nonce) {
        _nonce = uint256(bytes32(_rawNonce) & NONCE_MASK);
        _space = _rawNonce >> NONCE_BITS;
    }

    /**
     * @notice Returns the next nonce of the given nonce space
     * @param _space Nonce space, each space keeps an independent nonce count
     * @return The next nonce
     */
    function readNonce(uint256 _space) public view returns (uint256) {
        return
            uint256(StorageHelper.readBytes32Map(NONCE_KEY, bytes32(_space)));
    }

    /**
     * @notice Changes the next nonce of the given nonce space
     * @param _space Nonce space, each space keeps an independent nonce count
     * @param _nonce Nonce to write on the space
     */
    function _writeNonce(uint256 _space, uint256 _nonce) private {
        StorageHelper.writeBytes32Map(
            NONCE_KEY,
            bytes32(_space),
            bytes32(_nonce)
        );
    }

    /**
     * @notice Verify if a nonce is valid
     * @param _rawNonce Nonce to validate (may contain an encoded space)
     * @dev A valid nonce must be above the last one used
     *   with a maximum delta of 100
     */
    function _validateNonce(uint256 _rawNonce) internal {
        // Retrieve current nonce for this wallet
        (uint256 space, uint256 providedNonce) = _decodeNonce(_rawNonce);
        uint256 currentNonce = readNonce(space);

        // Verify if nonce is valid
        require(providedNonce == currentNonce, "INVALID_NONCE");

        // Update signature nonce
        uint256 newNonce = providedNonce + 1;
        _writeNonce(space, newNonce);
        emit NonceChange(space, newNonce);
    }
}
","keccak256":"0xe2ffeccd30c6b4fbea15c1470038659105d8bf80fd6d401375d63c0faa67c9d9","license":"LGPL-3.0-only"},"contracts/base/SessionManager.sol":{"content":"// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.7.0 <0.9.0;
import "../common/SelfAuthorized.sol";

contract SessionManager is SelfAuthorized {
    event AddedSession(address owner, bytes4 sessionKey);
    event RemovedSession(address owner, bytes4 sessionKey);

    bytes4 internal constant sessionPlatformPC = bytes4(keccak256("pc"));
    bytes4 internal constant sessionPlatformMobile =
        bytes4(keccak256("mobile"));
    bytes4 internal constant sessionPlatformWeb = bytes4(keccak256("web"));

    // kecc256("pc|mobile|web") => session pubkey address
    mapping(bytes4 => address) internal sessionByPlatform;

    function checkPlatform(bytes4 platform) internal pure {
        require(
            platform == sessionPlatformPC ||
                platform == sessionPlatformMobile ||
                platform == sessionPlatformWeb,
            "invalid platform"
        );
    }

    function internalAddSession(address owner, bytes4 platform) internal {
        checkPlatform(platform);
        sessionByPlatform[platform] = owner;
        emit AddedSession(owner, platform);
    }

    function addSession(address owner, bytes4 platform) public authorized {
        if (sessionByPlatform[platform] != address(0)) {
            removeSession(sessionByPlatform[platform], platform);
        }
        internalAddSession(owner, platform);
    }

    function removeSession(address owner, bytes4 platform) public authorized {
        require(
            sessionByPlatform[platform] == owner,
            "platform session expired"
        );
        sessionByPlatform[platform] = address(0);
        emit RemovedSession(owner, platform);
    }

    function isSessionOwner(address owner) public view returns (bool) {
        if (owner == address(0)) {
            return false;
        }
        address[] memory owners = getSessionOwners();
        for (uint256 i = 0; i < owners.length; i++) {
            if (owner == owners[i]) {
                return true;
            }
        }
        return false;
    }

    /// @dev Returns array of owners.
    /// @return Array of session owners.
    function getSessionOwners() public view returns (address[] memory) {
        address[] memory array = new address[](3);
        array[0] = sessionByPlatform[sessionPlatformPC];
        array[1] = sessionByPlatform[sessionPlatformMobile];
        array[2] = sessionByPlatform[sessionPlatformWeb];
        return array;
    }
}
